/**
 * Convert prestige good internal name to display name
 * @param {string} prestigeGoodName - Internal prestige good name
 * @returns {string} - Display name
 */
export function prestigeGoodToDisplayName(prestigeGoodName) {
    return prestigeGoodsMapping[prestigeGoodName] || prestigeGoodName.replace(/^prestige_good_/, '').replace(/_/g, ' ');
}

/**
 * Convert company internal name to display name
 * @param {string} companyName - Internal company name
 * @returns {string} - Display name
 */
export function companyToDisplayName(companyName) {
    return companyNamesMapping[companyName] || companyName.replace(/^company_/, '').replace(/_/g, ' ');
}

